import axios from 'axios';

const API_BASE_URL = 'http://localhost:8082/api';

const api = axios.create({
  baseURL: API_BASE_URL,
  timeout: 30000,
  headers: {
    'Content-Type': 'application/json',
  },
});

// 请求拦截器
api.interceptors.request.use(
  (config) => {
    console.log('API Request:', config.method.toUpperCase(), config.url, config.data);
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

// 响应拦截器
api.interceptors.response.use(
  (response) => {
    console.log('API Response:', response.status, response.data);
    return response;
  },
  (error) => {
    console.error('API Error:', error.response?.status, error.response?.data);
    return Promise.reject(error);
  }
);

// 消费贷相关API
export const consumerCreditAPI = {
  // 获取授信状态
  getStatus: (chatId) => api.get(`/consumer-credit/status?chatId=${chatId}`),

  // 完成步骤
  completeStep: (chatId, step) => api.post('/consumer-credit/step', { chatId, step }),

  // 初始化授信流程
  startCreditProcess: (chatId) => api.post('/consumer-credit/step', { chatId, step: 0 }),
};

export default api;
