import React from 'react';
import { Card, Button, Form, Input, Upload, Select, message } from 'antd';
import { UploadOutlined, UserOutlined, BankOutlined, FileTextOutlined } from '@ant-design/icons';

const { Option } = Select;

const CreditStepCard = ({ stepData, onComplete, loading }) => {
  // 只有在需要表单的步骤才创建表单实例
  const needsForm = stepData?.cardId && [
    'id_upload', 
    'set_password', 
    'bind_card', 
    'occupation', 
    'contact'
  ].includes(stepData.cardId);

  const [form] = needsForm ? Form.useForm() : [null];

  const renderStepContent = () => {
    switch (stepData?.cardId) {
      case 'live_detect':
        return (
          <div className="step-content">
            <h3>🎯 第一步：活体检测</h3>
            <p>请确保光线充足，正对摄像头进行人脸识别</p>
            <div style={{ textAlign: 'center', margin: '24px 0' }}>
              <div style={{ 
                width: 200, 
                height: 200, 
                background: '#f0f0f0', 
                borderRadius: 8,
                display: 'inline-flex',
                alignItems: 'center',
                justifyContent: 'center',
                fontSize: 48,
                color: '#999'
              }}>
                📷
              </div>
            </div>
            <p style={{ color: '#666', fontSize: 14 }}>
              点击"开始检测"按钮，系统将启动摄像头进行人脸识别
            </p>
          </div>
        );

      case 'id_upload':
        return (
          <div className="step-content">
            <h3>📄 第二步：上传身份证</h3>
            <Form form={form} layout="vertical">
              <Form.Item
                label="身份证正面"
                name="idFront"
                rules={[{ required: true, message: '请上传身份证正面' }]}
              >
                <Upload
                  maxCount={1}
                  accept="image/*"
                  beforeUpload={() => false}
                >
                  <Button icon={<UploadOutlined />}>上传身份证正面</Button>
                </Upload>
              </Form.Item>
              <Form.Item
                label="身份证反面"
                name="idBack"
                rules={[{ required: true, message: '请上传身份证反面' }]}
              >
                <Upload
                  maxCount={1}
                  accept="image/*"
                  beforeUpload={() => false}
                >
                  <Button icon={<UploadOutlined />}>上传身份证反面</Button>
                </Upload>
              </Form.Item>
            </Form>
          </div>
        );

      case 'set_password':
        return (
          <div className="step-content">
            <h3>🔐 第三步：设置交易密码</h3>
            <Form form={form} layout="vertical">
              <Form.Item
                label="交易密码"
                name="password"
                rules={[
                  { required: true, message: '请输入交易密码' },
                  { pattern: /^\d{6}$/, message: '请输入6位数字密码' }
                ]}
              >
                <Input.Password 
                  placeholder="请输入6位数字密码"
                  maxLength={6}
                />
              </Form.Item>
              <Form.Item
                label="确认密码"
                name="confirmPassword"
                rules={[
                  { required: true, message: '请确认交易密码' },
                  ({ getFieldValue }) => ({
                    validator(_, value) {
                      if (!value || getFieldValue('password') === value) {
                        return Promise.resolve();
                      }
                      return Promise.reject(new Error('两次输入的密码不一致'));
                    },
                  }),
                ]}
              >
                <Input.Password 
                  placeholder="请再次输入密码"
                  maxLength={6}
                />
              </Form.Item>
            </Form>
          </div>
        );

      case 'sign_agreement':
        return (
          <div className="step-content">
            <h3>📋 第四步：签署授信协议</h3>
            <div style={{ 
              background: '#f6ffed', 
              border: '1px solid #b7eb8f', 
              borderRadius: 6,
              padding: 16,
              marginBottom: 16
            }}>
              <h4>《消费贷授信协议》</h4>
              <div style={{ maxHeight: 200, overflow: 'auto', marginTop: 12 }}>
                <p>1. 授信额度：最高20万元</p>
                <p>2. 年化利率：7.2% - 18%（根据信用评估）</p>
                <p>3. 还款期限：6-36个月</p>
                <p>4. 提前还款：支持随时提前还款，无违约金</p>
                <p>5. 逾期处理：按日收取逾期利息</p>
                <p>6. 个人信息使用：仅用于授信评估和风险管理</p>
              </div>
            </div>
            <Form form={form}>
              <Form.Item
                name="agreement"
                valuePropName="checked"
                rules={[{ validator: (_, value) => value ? Promise.resolve() : Promise.reject(new Error('请同意协议')) }]}
              >
                <input type="checkbox" id="agreement" />
                <label htmlFor="agreement" style={{ marginLeft: 8 }}>
                  我已阅读并同意《消费贷授信协议》
                </label>
              </Form.Item>
            </Form>
          </div>
        );

      case 'bind_card':
        return (
          <div className="step-content">
            <h3>💳 第五步：绑定银行卡</h3>
            <Form form={form} layout="vertical">
              <Form.Item
                label="银行卡号"
                name="cardNumber"
                rules={[
                  { required: true, message: '请输入银行卡号' },
                  { pattern: /^\d{16,19}$/, message: '请输入正确的银行卡号' }
                ]}
              >
                <Input placeholder="请输入银行卡号" maxLength={19} />
              </Form.Item>
              <Form.Item
                label="开户银行"
                name="bankName"
                rules={[{ required: true, message: '请选择开户银行' }]}
              >
                <Select placeholder="请选择开户银行">
                  <Option value="ICBC">中国工商银行</Option>
                  <Option value="ABC">中国农业银行</Option>
                  <Option value="BOC">中国银行</Option>
                  <Option value="CCB">中国建设银行</Option>
                  <Option value="BOCOM">交通银行</Option>
                  <Option value="CMB">招商银行</Option>
                  <Option value="SPDB">浦发银行</Option>
                  <Option value="CIB">兴业银行</Option>
                </Select>
              </Form.Item>
              <Form.Item
                label="预留手机号"
                name="phone"
                rules={[
                  { required: true, message: '请输入预留手机号' },
                  { pattern: /^1[3-9]\d{9}$/, message: '请输入正确的手机号' }
                ]}
              >
                <Input placeholder="请输入银行预留手机号" maxLength={11} />
              </Form.Item>
            </Form>
          </div>
        );

      case 'occupation':
        return (
          <div className="step-content">
            <h3>💼 第六步：填写职业信息</h3>
            <Form form={form} layout="vertical">
              <Form.Item
                label="职业类型"
                name="occupationType"
                rules={[{ required: true, message: '请选择职业类型' }]}
              >
                <Select placeholder="请选择职业类型">
                  <Option value="employee">上班族</Option>
                  <Option value="business">个体户</Option>
                  <Option value="freelance">自由职业</Option>
                  <Option value="student">学生</Option>
                  <Option value="other">其他</Option>
                </Select>
              </Form.Item>
              <Form.Item
                label="单位名称"
                name="companyName"
                rules={[{ required: true, message: '请输入单位名称' }]}
              >
                <Input placeholder="请输入单位名称" />
              </Form.Item>
              <Form.Item
                label="月收入（元）"
                name="monthlyIncome"
                rules={[
                  { required: true, message: '请选择月收入' },
                  { pattern: /^\d+$/, message: '请输入数字' }
                ]}
              >
                <Select placeholder="请选择月收入">
                  <Option value="3000-5000">3000-5000</Option>
                  <Option value="5000-8000">5000-8000</Option>
                  <Option value="8000-12000">8000-12000</Option>
                  <Option value="12000-20000">12000-20000</Option>
                  <Option value="20000+">20000以上</Option>
                </Select>
              </Form.Item>
            </Form>
          </div>
        );

      case 'contact':
        return (
          <div className="step-content">
            <h3>👥 第七步：填写联系人</h3>
            <Form form={form} layout="vertical">
              <div style={{ marginBottom: 24 }}>
                <h4>联系人1（必填）</h4>
                <Form.Item
                  label="姓名"
                  name={['contact1', 'name']}
                  rules={[{ required: true, message: '请输入联系人姓名' }]}
                >
                  <Input placeholder="请输入联系人姓名" />
                </Form.Item>
                <Form.Item
                  label="手机号"
                  name={['contact1', 'phone']}
                  rules={[
                    { required: true, message: '请输入联系人手机号' },
                    { pattern: /^1[3-9]\d{9}$/, message: '请输入正确的手机号' }
                  ]}
                >
                  <Input placeholder="请输入联系人手机号" maxLength={11} />
                </Form.Item>
                <Form.Item
                  label="关系"
                  name={['contact1', 'relation']}
                  rules={[{ required: true, message: '请选择关系' }]}
                >
                  <Select placeholder="请选择关系">
                    <Option value="family">家人</Option>
                    <Option value="friend">朋友</Option>
                    <Option value="colleague">同事</Option>
                  </Select>
                </Form.Item>
              </div>
              <div>
                <h4>联系人2（必填）</h4>
                <Form.Item
                  label="姓名"
                  name={['contact2', 'name']}
                  rules={[{ required: true, message: '请输入联系人姓名' }]}
                >
                  <Input placeholder="请输入联系人姓名" />
                </Form.Item>
                <Form.Item
                  label="手机号"
                  name={['contact2', 'phone']}
                  rules={[
                    { required: true, message: '请输入联系人手机号' },
                    { pattern: /^1[3-9]\d{9}$/, message: '请输入正确的手机号' }
                  ]}
                >
                  <Input placeholder="请输入联系人手机号" maxLength={11} />
                </Form.Item>
                <Form.Item
                  label="关系"
                  name={['contact2', 'relation']}
                  rules={[{ required: true, message: '请选择关系' }]}
                >
                  <Select placeholder="请选择关系">
                    <Option value="family">家人</Option>
                    <Option value="friend">朋友</Option>
                    <Option value="colleague">同事</Option>
                  </Select>
                </Form.Item>
              </div>
            </Form>
          </div>
        );

      case 'assess_wait':
        return (
          <div className="step-content">
            <h3>⏳ 第八步：系统评估中</h3>
            <div style={{ textAlign: 'center', margin: '48px 0' }}>
              <div style={{ fontSize: 64, marginBottom: 24 }}>🤖</div>
              <h4>系统正在评估您的授信申请</h4>
              <p style={{ color: '#666', marginTop: 16 }}>
                请稍等片刻，评估过程大约需要30秒...
              </p>
              <div style={{ 
                width: '80%', 
                height: 4, 
                background: '#f0f0f0', 
                borderRadius: 2,
                margin: '24px auto',
                overflow: 'hidden'
              }}>
                <div style={{
                  width: '60%',
                  height: '100%',
                  background: '#1890ff',
                  borderRadius: 2,
                  animation: 'loading 2s ease-in-out infinite'
                }}></div>
              </div>
            </div>
          </div>
        );

      default:
        return (
          <div className="step-content">
            <h3>{stepData?.title || '未知步骤'}</h3>
            <p>{stepData?.content || '正在加载步骤信息...'}</p>
          </div>
        );
    }
  };

  const handleComplete = async () => {
    try {
      if (stepData?.cardId === 'live_detect' || stepData?.cardId === 'sign_agreement' || stepData?.cardId === 'assess_wait') {
        // 不需要表单验证的步骤
        onComplete();
      } else {
        // 需要表单验证的步骤
        if (form) {
          await form.validateFields();
          const formData = form.getFieldsValue();
          console.log('表单数据:', formData);
          onComplete(formData);
        } else {
          // 如果没有表单实例，直接调用onComplete
          onComplete();
        }
      }
    } catch (error) {
      console.error('表单验证失败:', error);
      message.error('请完善必填信息');
    }
  };

  return (
    <Card className="step-card">
      {renderStepContent()}
      <div className="action-buttons">
        <Button 
          type="primary" 
          size="large" 
          onClick={handleComplete}
          loading={loading}
          style={{ minWidth: 120 }}
        >
          {stepData?.cardId === 'assess_wait' ? '等待评估完成' : '下一步'}
        </Button>
      </div>
    </Card>
  );
};

export default CreditStepCard;
