import React, { useState, useEffect } from 'react';
import { Steps, message, Spin, Button, Card } from 'antd';
import { ReloadOutlined } from '@ant-design/icons';
import CreditStepCard from './components/CreditStepCard';
import SuccessPage from './components/SuccessPage';
import { consumerCreditAPI } from './services/api';
import './App.css';
import ChatWindow from './components/ChatWindow';

const { Step } = Steps;

function App() {
  const [currentStep, setCurrentStep] = useState(0);
  const [chatId, setChatId] = useState('');
  const [loading, setLoading] = useState(false);
  const [stepData, setStepData] = useState(null);
  const [authorized, setAuthorized] = useState(false);
  const [error, setError] = useState(null);
  const [messages, setMessages] = useState([]);

  // 生成唯一会话ID
  useEffect(() => {
    const generateChatId = () => {
      const timestamp = Date.now();
      const random = Math.random().toString(36).substr(2, 9);
      return `user_${timestamp}_${random}`;
    };

    const id = generateChatId();
    setChatId(id);
    console.log('生成会话ID:', id);
  }, []);

  // 初始化授信流程
  useEffect(() => {
    if (chatId) {
      initializeCreditProcess();
    }
  }, [chatId]);

  const initializeCreditProcess = async () => {
    try {
      setLoading(true);
      setError(null);
      const response = await consumerCreditAPI.startCreditProcess(chatId);
      if (response.data && response.data.card) {
        setStepData(response.data.card);
        setCurrentStep(0);
        addAssistantMessage(response.data.card.title || '流程开始');
      } else {
        throw new Error('响应格式错误：缺少card字段');
      }
    } catch (error) {
      setError('初始化授信流程失败');
      message.error('初始化授信流程失败');
    } finally {
      setLoading(false);
    }
  };

  const handleStepComplete = async (formData) => {
    try {
      setLoading(true);
      console.log(`完成步骤 ${currentStep}:`, formData);

      // 调用后端完成当前步骤
      const response = await consumerCreditAPI.completeStep(chatId, currentStep);
      console.log('步骤完成响应:', response.data);

      if (response.data && response.data.card) {
        const nextCard = response.data.card;

        // 检查是否已完成所有步骤 - 检查状态或卡片ID
        if (nextCard.state === 'COMPLETED' || nextCard.state === 'CREDIT_DONE' || nextCard.id === 'consumer_loan_offers') {
          setAuthorized(true);
          message.success('授信申请已通过！');
        } else {
          // 进入下一步
          setStepData(nextCard);
          setCurrentStep(currentStep + 1);
          message.success('步骤完成，进入下一步');
        }
      } else {
        console.error('响应格式错误:', response.data);
        throw new Error('响应格式错误：缺少card字段');
      }
    } catch (error) {
      console.error('步骤完成失败:', error);
      message.error('步骤完成失败，请重试');
    } finally {
      setLoading(false);
    }
  };

  const handleSelectOffer = (offer) => {
    message.success(`已选择借款方案：${(offer.amount / 10000).toFixed(1)}万元`);
    // 这里可以添加跳转到借款确认页面的逻辑
  };

  const handleRefresh = () => {
    window.location.reload();
  };

  const getStepTitle = (step) => {
    const titles = [
      '活体检测',
      '上传身份证',
      '设置密码',
      '签署协议',
      '绑定银行卡',
      '职业信息',
      '联系人',
      '系统评估',
      '授信完成'
    ];
    return titles[step] || `步骤${step + 1}`;
  };

  const addAssistantMessage = (content) => {
    setMessages((prev) => [...prev, { role: 'assistant', content }]);
  };
  const addUserMessage = (content) => {
    setMessages((prev) => [...prev, { role: 'user', content }]);
  };

  const handleChatSend = async (text) => {
    addUserMessage(text);
  };
  const handleChatConfirm = async () => {
    await handleStepComplete();
  };

  if (error) {
    return (
      <div className="error-container">
        <Card>
          <div style={{ textAlign: 'center', padding: '48px' }}>
            <div style={{ fontSize: 64, marginBottom: 24 }}>❌</div>
            <h3>出错了</h3>
            <p style={{ color: '#666', marginBottom: 24 }}>{error}</p>
            <Button 
              type="primary" 
              icon={<ReloadOutlined />}
              onClick={handleRefresh}
            >
              刷新页面
            </Button>
          </div>
        </Card>
      </div>
    );
  }

  if (loading && !stepData) {
    return (
      <div className="loading-container">
        <Spin size="large" spinning={true}>
          <div style={{ height: 200 }} />
        </Spin>
      </div>
    );
  }

  if (authorized) {
    return <SuccessPage onSelectOffer={handleSelectOffer} chatId={chatId} />;
  }

  return (
    <div className="App">
      <div className="container">
        {/* 对话窗口 */}
        <ChatWindow
          messages={messages}
          onSend={handleChatSend}
          stepData={stepData}
          onComplete={handleStepComplete}
          loading={loading}
        />
        {/* 原有进度与步骤卡片 */}
        <div className="progress-container">
          <Steps current={currentStep} size="small">
            {Array.from({ length: 8 }, (_, i) => (
              <Step key={i} title={getStepTitle(i)} />
            ))}
          </Steps>
        </div>

        {/* 去除底部独立的步骤卡片，因为卡片已在对话中展示，可保留或移除。这里选择保留以便全宽显示。 */}
        {stepData && (
          <CreditStepCard 
            stepData={stepData}
            onComplete={handleStepComplete}
            loading={loading}
          />
        )}
      </div>
    </div>
  );
}

export default App;
