import React, { useState, useEffect } from 'react';
import { Steps, message, Spin, Button, Card } from 'antd';
import { ReloadOutlined } from '@ant-design/icons';
import CreditStepCard from './components/CreditStepCard';
import SuccessPage from './components/SuccessPage';
import { consumerCreditAPI } from './services/api';
import './App.css';

const { Step } = Steps;

function App() {
  const [currentStep, setCurrentStep] = useState(0);
  const [chatId, setChatId] = useState('');
  const [loading, setLoading] = useState(false);
  const [stepData, setStepData] = useState(null);
  const [authorized, setAuthorized] = useState(false);
  const [error, setError] = useState(null);

  // 生成唯一会话ID
  useEffect(() => {
    const generateChatId = () => {
      const timestamp = Date.now();
      const random = Math.random().toString(36).substr(2, 9);
      return `user_${timestamp}_${random}`;
    };

    const id = generateChatId();
    setChatId(id);
    console.log('生成会话ID:', id);
  }, []);

  // 初始化授信流程
  useEffect(() => {
    if (chatId) {
      initializeCreditProcess();
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [chatId]);

  const initializeCreditProcess = async () => {
    try {
      setLoading(true);
      setError(null);

      console.log('开始初始化授信流程，chatId:', chatId);

      // 开始授信流程
      const response = await consumerCreditAPI.startCreditProcess(chatId);
      console.log('初始化响应状态:', response.status);
      console.log('初始化响应数据:', response.data);

      if (response.data && response.data.card) {
        setStepData(response.data.card);
        setCurrentStep(0);
        console.log('初始化成功，步骤数据:', response.data.card);
      } else {
        console.warn('初始化响应格式异常:', response.data);
        throw new Error('响应格式错误：缺少card字段');
      }
    } catch (error) {
      console.error('初始化失败详细信息:', error);
      console.error('错误类型:', error.name);
      console.error('错误消息:', error.message);
      console.error('错误堆栈:', error.stack);
      if (error.response) {
        console.error('HTTP响应状态:', error.response.status);
        console.error('HTTP响应数据:', error.response.data);
      }

      let errorMsg = '初始化授信流程失败';
      if (error.message) {
        errorMsg += `：${error.message}`;
      }
      if (error.response && error.response.status) {
        errorMsg += ` (HTTP ${error.response.status})`;
      }

      setError(errorMsg);
      message.error(errorMsg);
    } finally {
      setLoading(false);
    }
  };

  const handleStepComplete = async (formData) => {
    try {
      setLoading(true);
      console.log(`完成步骤 ${currentStep}:`, formData);

      // 调用后端完成当前步骤
      const response = await consumerCreditAPI.completeStep(chatId, currentStep);
      console.log('步骤完成响应:', response.data);

      if (response.data && response.data.card) {
        const nextCard = response.data.card;

        // 检查是否已完成所有步骤
        if (nextCard.state === 'COMPLETED' || nextCard.cardId === 'consumer_loan_offers') {
          setAuthorized(true);
          message.success('授信申请已通过！');
        } else {
          // 进入下一步
          setStepData(nextCard);
          setCurrentStep(currentStep + 1);
          message.success('步骤完成，进入下一步');
        }
      }
    } catch (error) {
      console.error('步骤完成失败:', error);
      message.error('步骤完成失败，请重试');
    } finally {
      setLoading(false);
    }
  };

  const handleSelectOffer = (offer) => {
    message.success(`已选择借款方案：${(offer.amount / 10000).toFixed(1)}万元`);
    // 这里可以添加跳转到借款确认页面的逻辑
  };

  const handleRefresh = () => {
    window.location.reload();
  };

  const getStepTitle = (step) => {
    const titles = [
      '活体检测',
      '上传身份证',
      '设置密码',
      '签署协议',
      '绑定银行卡',
      '职业信息',
      '联系人',
      '系统评估',
      '授信完成'
    ];
    return titles[step] || `步骤${step + 1}`;
  };

  if (error) {
    return (
      <div className="error-container">
        <Card>
          <div style={{ textAlign: 'center', padding: '48px' }}>
            <div style={{ fontSize: 64, marginBottom: 24 }}>❌</div>
            <h3>出错了</h3>
            <p style={{ color: '#666', marginBottom: 24 }}>{error}</p>
            <Button 
              type="primary" 
              icon={<ReloadOutlined />}
              onClick={handleRefresh}
            >
              刷新页面
            </Button>
          </div>
        </Card>
      </div>
    );
  }

  if (loading && !stepData) {
    return (
      <div className="loading-container">
        <Spin size="large" spinning={true}>
          <div style={{ height: 200 }} />
        </Spin>
      </div>
    );
  }

  if (authorized) {
    return <SuccessPage onSelectOffer={handleSelectOffer} chatId={chatId} />;
  }

  return (
    <div className="App">
      <div className="container">
        <div className="progress-container">
          <Steps current={currentStep} size="small">
            {Array.from({ length: 8 }, (_, i) => (
              <Step key={i} title={getStepTitle(i)} />
            ))}
          </Steps>
        </div>

        {stepData && (
          <CreditStepCard 
            stepData={stepData}
            onComplete={handleStepComplete}
            loading={loading}
          />
        )}
      </div>
    </div>
  );
}

export default App;
